import React, { useState } from "react";
import { Button } from "@material-ui/core";
import Square from "./../components/square";

function Game() {
  const [squares, setsquares] = useState(Array(9).fill(null));
  // function abc() {
  //   if (state == "X") {
  //     setstate("O");
  //   } else {
  //     setstate("1");
  //   }
  // }
  function renderSquare(i: number) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          const nextSquares = squares.slice();
          nextSquares[i] == "X";
          setsquares(nextSquares);
        }}
      ></Square>
    );
  }

  return (
    <div>
      {renderSquare(0)}
      {renderSquare(1)}
      {renderSquare(2)}
      <br></br>
      {renderSquare(3)}
      {renderSquare(4)}
      {renderSquare(5)}
      <br></br>
      {renderSquare(6)}
      {renderSquare(7)}
      {renderSquare(8)}
      <br></br>
    </div>
  );
}

export default Game;
