import React, { useState } from "react";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";
import Square from "./../components/square";
import square from "./../components/square";
import Alert from "@material-ui/lab/Alert";
const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: "100%",
      "& > * + *": {
        marginTop: theme.spacing(2)
      }
    },
    row: {
      display: "inline"
    }
  })
);

function Game() {
  const classes = useStyles();
  const [squares, setsquares] = useState(Array(9).fill(null));
  const [isXNext, setXNext] = useState(true);

  function renderSquare(i: number) {
    return (
      <Square
        onClick={() => {
          if (squares[i] != null) {
            return;
          }
          const nextSquares = squares.slice();
          nextSquares[i] = isXNext ? "X" : "O";
          setXNext(!isXNext);
          setsquares(nextSquares);
        }}
        value={squares[i]}
      ></Square>
    );
  }

  function winner(squares) {
    const winninglines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let j = 0; j < winninglines.length; j++) {
      const [a, b, c] = winninglines[j];
      if (squares[a] && squares[a] == squares[b] && squares[c] == squares[a]) {
        return square[a];
      } else return null;
    }
  }
  function isBoardFull() {
    for (let k = 0; k < squares.length; k++) {
      if (squares[k] == null) return false;
      else return true;
    }
  }
  const Winner = winner(squares);

  function getstatus() {
    if (Winner) {
      return "winner is " + Winner;
    } else if (isBoardFull) {
      return "game is Draw";
    } else return "next turn is" + (isXNext ? "X" : "O");
  }

  return (
    <div>
      <div className={classes.row}>
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className={classes.row}>
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className={classes.row}>
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>

      <Alert variant="filled" severity="info">
        {getstatus()}
      </Alert>
    </div>
  );
}

export default Game;
